#!/bin/bash# Test script for Portal AI Music production deploymentecho "üéµ Testing Portal AI Music Production Deployment"echo "================================================"BASE_URL="https://portal-ai-music.vercel.app"echo "1. Testing Frontend..."FRONTEND_STATUS=$(curl -s -o /dev/null -w "%{http_code}" "$BASE_URL")if [ "$FRONTEND_STATUS" = "200" ]; then    echo "‚úÖ Frontend is accessible (Status: $FRONTEND_STATUS)"else    echo "‚ùå Frontend not accessible (Status: $FRONTEND_STATUS)"fiecho ""echo "2. Testing API Health endpoint..."HEALTH_RESPONSE=$(curl -s "$BASE_URL/api/health")if echo "$HEALTH_RESPONSE" | grep -q "status.*healthy"; then    echo "‚úÖ API Health endpoint working"    echo "$HEALTH_RESPONSE"else    echo "‚ùå API Health endpoint not working"    echo "Response: $HEALTH_RESPONSE"fiecho ""echo "3. Testing API Genres endpoint..."GENRES_RESPONSE=$(curl -s "$BASE_URL/api/genres")if echo "$GENRES_RESPONSE" | grep -q "genres"; then    echo "‚úÖ API Genres endpoint working"    echo "$GENRES_RESPONSE" | head -100else    echo "‚ùå API Genres endpoint not working"    echo "Response: $GENRES_RESPONSE"fiecho ""echo "4. Testing Music Generation..."GENERATE_RESPONSE=$(curl -s -X POST "$BASE_URL/api/generate-music" \  -H "Content-Type: application/json" \  -d '{"duration": 5, "genre": "pop", "mood": "upbeat"}' \  -w "HTTP_CODE:%{http_code}")HTTP_CODE=$(echo "$GENERATE_RESPONSE" | grep -o 'HTTP_CODE:[0-9]*' | cut -d: -f2)RESPONSE_BODY=$(echo "$GENERATE_RESPONSE" | sed 's/HTTP_CODE:[0-9]*$//')if [ "$HTTP_CODE" = "200" ]; then    echo "‚úÖ Music Generation API working (Status: $HTTP_CODE)"    echo "$RESPONSE_BODY"else    echo "‚ùå Music Generation API not working (Status: $HTTP_CODE)"    echo "Response: $RESPONSE_BODY"fiecho ""echo "5. Testing with cache-busting..."CACHE_BUST_HEALTH=$(curl -s "$BASE_URL/api/health?t=$(date +%s)")if echo "$CACHE_BUST_HEALTH" | grep -q "status.*healthy"; then    echo "‚úÖ Cache-busting works for health endpoint"else    echo "‚ùå Cache-busting not working for health endpoint"    echo "Response: $CACHE_BUST_HEALTH"fiecho ""echo "üéµ Test Complete"echo "================================================"